    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        int i = Integer.parseInt(jTextField1.getText());
        if(i<=10){
            
          JOptionPane.showMessageDialog(null, "NOOOO 😰 DON'T DO THIS");

        }else if(i>10 && i<=15){
            
         JOptionPane.showMessageDialog(null, "MEH... 😑 ");

        }else if(i>15 && i<20){
            
         JOptionPane.showMessageDialog(null, "GOOD...NOT GOOD ENOUGH THOUGH 😒");

        }else if(i==20){
            
         JOptionPane.showMessageDialog(null, "YOOHOOO YOU'RE THE BEST 😍");

         }else if(i>20){
         JOptionPane.showMessageDialog(null, "Well I wish but unfortunately That's not Possible 😒");

        }
        
    }//GEN-LAST:event_jButton1ActionPerformed

    private void jTextField1FocusGained(java.awt.event.FocusEvent evt) {//GEN-FIRST:event_jTextField1FocusGained
        if(jTextField1.getText().equals("Ps: Marks Not Money 😈"))
        jTextField1.setText("");
    }//GEN-LAST:event_jTextField1FocusGained

    private void jTextField1FocusLost(java.awt.event.FocusEvent evt) {//GEN-FIRST:event_jTextField1FocusLost
        if(jTextField1.getText().equals(""))
        jTextField1.setText("Ps: Marks Not Money 😈");
    }//GEN-LAST:event_jTextField1FocusLost

